import * as Messages from '../helpers/console-messages';
import * as Models from '../models/models';
import * as jsonFuture from 'json-future';
import type * as messages from '@cucumber/messages';
import * as path from 'path';
import { format, intervalToDuration } from 'date-fns';
import fs, { promises as fsp } from 'fs';
import type { Readable } from 'stream';
import type { Stats } from 'fs';
import os from 'os';

export const streamToArray = async ( readableStream: Readable ): Promise<messages.Envelope[]> => {
  return new Promise<messages.Envelope[]>(
    (
      resolve: ( wrappers: messages.Envelope[] ) => void,
      reject: ( err: Error ) => void
    ) => {
      const items: messages.Envelope[] = [];
      readableStream.on( 'data', ( item: messages.Envelope ) => {
        items.push( item );
      } );
      /* istanbul ignore next */
      readableStream.on( 'error', ( err: Error ) => {
        reject( err );
      } );
      readableStream.on( 'end', () => {
        resolve( items );
      } );
    }
  );
};

export const updatePercentages = ( results: Models.FeatureModuleResults | Models.ModuleResults ): Models.FeatureModuleResults | Models.ModuleResults => {
  results.ambiguousPercentage = ( results.ambiguous ? results.ambiguous / results.total * 100 : 0 ).toFixed( 2 );
  results.failedPercentage = ( results.failed ? results.failed / results.total * 100 : 0 ).toFixed( 2 );
  results.undefinedPercentage = ( results.undefined ? results.undefined / results.total * 100 : 0 ).toFixed( 2 );
  results.passedPercentage = ( results.passed ? results.passed / results.total * 100 : 0 ).toFixed( 2 );
  results.pendingPercentage = ( results.pending ? results.pending / results.total * 100 : 0 ).toFixed( 2 );
  results.skippedPercentage = ( results.skipped ? results.skipped / results.total * 100 : 0 ).toFixed( 2 );
  if ( 'variousPercentage' in results ) {
    results.variousPercentage = ( results.various ? results.various / results.total * 100 : 0 ).toFixed( 2 );
    return results;
  }
  return results;
};

export const updateResultsAndPercentages = ( sourceResults: Models.ModuleResults, targetResults: Models.ModuleResults ): Models.ModuleResults => {
  sourceResults.passed += targetResults.passed;
  sourceResults.failed += targetResults.failed;
  sourceResults.skipped += targetResults.skipped;
  sourceResults.pending += targetResults.pending;
  sourceResults.undefined += targetResults.undefined;
  sourceResults.ambiguous += targetResults.ambiguous;
  sourceResults.total += targetResults.total;

  sourceResults.ambiguousPercentage = ( sourceResults.ambiguous ? sourceResults.ambiguous / sourceResults.total * 100 : 0 ).toFixed( 2 );
  sourceResults.failedPercentage = ( sourceResults.failed ? sourceResults.failed / sourceResults.total * 100 : 0 ).toFixed( 2 );
  sourceResults.undefinedPercentage = ( sourceResults.undefined ? sourceResults.undefined / sourceResults.total * 100 : 0 ).toFixed( 2 );
  sourceResults.passedPercentage = ( sourceResults.passed ? sourceResults.passed / sourceResults.total * 100 : 0 ).toFixed( 2 );
  sourceResults.pendingPercentage = ( sourceResults.pending ? sourceResults.pending / sourceResults.total * 100 : 0 ).toFixed( 2 );
  sourceResults.skippedPercentage = ( sourceResults.skipped ? sourceResults.skipped / sourceResults.total * 100 : 0 ).toFixed( 2 );
  return sourceResults;
};

export const updateStatus = ( featureResults: Models.FeatureResults ): Models.Result[] => {
  const result = <Models.Result[]>[];

  if ( featureResults.scenarios.ambiguous ) {
    result.push( { icon: 'fas fa-question-circle ambiguous-color', status: Models.Status.ambiguous } );
  }

  if ( featureResults.scenarios.pending ) {
    result.push( { icon: 'fas fa-question-circle pending-color', status: Models.Status.pending } );
  }

  if ( featureResults.scenarios.undefined ) {
    result.push( { icon: 'fa fa-question-circle undefined-color', status: Models.Status.undefined } );
  }

  if ( featureResults.scenarios.skipped ) {
    result.push( { icon: 'arrow-circle-right skipped-color', status: Models.Status.skipped } );
  }

  if ( featureResults.scenarios.failed ) {
    result.push( { icon: 'exclamation-circle failed-color', status: Models.Status.failed } );
  }

  if ( featureResults.scenarios.passed ) {
    result.push( { icon: 'check-circle passed-color', status: Models.Status.passed } );
  }

  return result;
};

export const isMetadata = ( metadata: Models.Metadata[] | undefined ): metadata is Models.Metadata[] =>
  Array.isArray( metadata )
    ? !metadata.filter( metadataElement => ( !( 'name' in metadataElement ) || !( 'value' in metadataElement ) ) ).length
    : false;

export const escapeHTML = ( value: string ): string => value
  .replace( /&/gu, '&amp;' )
  .replace( /</gu, '&lt;' )
  .replace( />/gu, '&gt;' );

export const isBase64 = ( value: string ): boolean => {
  const base64regex = /^(?<firstGroup>[0-9a-zA-Z+/]{4})*(?<secondGroup>(?<thirdGroup>[0-9a-zA-Z+/]{2}==)|(?<fourthGroup>[0-9a-zA-Z+/]{3}=))?$/u;

  return base64regex.test( value );
};

export const getFilesAsync = async ( dir: string ): Promise<string[]> => {
  let files: string[] = [];
  const getFiles = async ( folder: string ): Promise<string[]> => {
    files = await fsp.readdir( folder );
    const result: Promise<string[]>[] = files.map( async ( file ) => {
      const fileNameAndPath = path.join( folder, file );
      return fsp.stat( fileNameAndPath ).then( ( stat ) => ( stat.isDirectory() ? getFiles( fileNameAndPath ) : [ fileNameAndPath ] ) );
    } );
    return <string[]>Array.prototype.concat( ...( await Promise.all( result ) ) );
  };

  return getFiles( dir );
};

export const checkFolder = ( file: string | undefined ): boolean => {
  if ( typeof file === 'undefined' ) {
    return false;
  }
  let stats = <Stats>{};
  try {
    stats = fs.statSync( file );
  } catch ( error: unknown ) {
    throw new Error( Messages.invalidPathProvided( file ) );
  }

  return stats.isDirectory();
};

export const readJsonFile = async <T> ( file: string, encoding: BufferEncoding = 'utf8' ): Promise<T | null> => {
  const report = await fsp.readFile( file, { encoding } );

  try {
    return <T>jsonFuture.parse( report );
  } catch ( error: unknown ) {
    console.log( ( Messages.invalidJsonProvided( file, ( <Error>error ).message ) ) );
  }
  return null;
};

export const saveJsonFile = async<T> ( filePath: string | undefined, fileName: string, json: T ): Promise<boolean> => {
  const fileNameAndPath = path.join( filePath!, fileName );
  const jsonString = await jsonFuture.stringifyAsync( { data: json, replacer: null, space: 4 } );
  await fsp.writeFile( fileNameAndPath, jsonString, { encoding: 'utf8' } );
  return true;
};

export const getDateFormatted = ( date: Date ): string => format( date, 'yyyy-MM-dd HH:mm:ss' );

export const convertTimeFromNanoSecondsToHHMMSS = ( time: number ): string => {
  const duration = intervalToDuration( { start: 0, end: time / 1000000 } );
  const hours = ( `${String( duration.hours )}` ).padStart( 2, '0' );
  const minutes = ( `${String( duration.minutes )}` ).padStart( 2, '0' );
  const seconds = ( `${String( duration.seconds )}` ).padStart( 2, '0' );

  return `${hours}:${minutes}:${seconds}`;
};

export const arraymove = <T> ( arr: T[], fromIndex: number, toIndex: number ): T[] => {
  const element = arr[ fromIndex ];
  const deleteCountOne = 1;
  arr.splice( fromIndex, deleteCountOne );
  const deleteCountZero = 0;
  arr.splice( toIndex, deleteCountZero, element );
  return arr;
};

export const exists = ( filePath: string ): boolean => {
  return fs.existsSync( filePath );
};

export const initializePath = ( reportPath?: string | undefined ): string => {
  let reportPathFixed = reportPath;
  const resultCheckReportCreationPath = checkFolder( reportPath );

  if ( !resultCheckReportCreationPath && typeof reportPath === 'undefined' ) {
    const date = format( new Date(), 'yyyy-MM-dd__HH-mm-ss' );
    reportPathFixed = path.join( os.tmpdir(), 'cucumber-html-report-generator', date );
    fs.mkdirSync( reportPathFixed, { recursive: true } );
  }

  return reportPathFixed!;
};

export const pathExists = async ( folderPath: string ): Promise<boolean> => {
  return new Promise( ( resolve ) => {
    fs.access( folderPath, fs.constants.F_OK, error => {
      resolve( !error );
    } );
  } );
};

export const emptyFolder = async ( folderPath: string ): Promise<void> => {
  /* istanbul ignore next */
  if ( await pathExists( folderPath ) ) {
    await fsp.rm( folderPath, { recursive: true } );
  }
  await fsp.mkdir( folderPath, { recursive: true } );
};

export const reportCreated = ( featureId: string ):string => `\n 
    =====================================================================================
        Multiple Cucumber HTML report generated in:
        ${featureId}
\n
    =====================================================================================\n`;
export const jsonPathNotProvided = 'A path which holds the JSON files should be provided.';
export const optionsNotProvided = 'Options need to be provided.';
export const noJsonFilesProvided = ( jsonDir: string ): string => `No JSON files found in '${jsonDir}'. NO REPORT CAN BE CREATED!`; 
export const invalidJsonProvided = ( fileNameAndPath: string, errorMessage: string ): string => `The file ${fileNameAndPath} is not a valid json, it has the error message ${errorMessage}`; 
export const invalidPathProvided = ( filePath: string ):string => `The path provided: '${filePath}' is invalid`;
export const featureRemoved = ( featureName: string ): string => `The feature ${featureName} has been removed because it doesn't have any scenarios`;
export const scenarioWithoutIdRemoved = ( scenarioName: string , featureName: string ): string => `The scenario ${scenarioName} of the feature: ${featureName} has been removed as it doesn't have id.`;
export const scenarioWithoutStepsRemoved = ( scenarioName: string , featureName: string ): string => `The scenario ${scenarioName} of the feature: ${featureName} has been removed as it doesn't have steps.`;

import type * as Models from '../models/models';
import { CollectFeatureFiles } from '../feature-collector/feature-collector';
import { CollectJsons } from '../collect-jsons/collect-jsons';
import { GenerateHtml } from './helpers/generate-html';
import { SuiteFactory } from './ReportObjectsFormatters/suite-formatter';
import axios from 'axios';
import { userPropertiesValidation } from '../helpers/application-properties-validation';

export const generateHtmlReport = async ( reportConfiguration: Models.ReportDisplay | undefined, jsonReport: Models.ExtendedReport ): Promise<void> =>{
  const reportConfigurationInitialized = userPropertiesValidation.initializeReportDisplayParameters( reportConfiguration );
  await new GenerateHtml( jsonReport, reportConfigurationInitialized ).createHtmlPages();
};

export const insertReportIntoDatabase = async ( jsonReport: Models.ExtendedReport, userProperties: Models.ReportGeneration ): Promise<string | null> =>{
  return ( await axios.request<Models.Response>( {
    transitional:{
      silentJSONParsing: false
    },
    responseType: 'json',
    method: 'POST',
    url: `${userProperties.mongooseServerUrl!}/insertReport`,
    data: jsonReport,
    maxRedirects: 0,
    maxBodyLength: Infinity, 
    maxContentLength: Infinity,
    headers:{
      'content-type': 'application/json'
    }
  } ) ).data.reportId;
};

export const generate = async ( userProperties: Models.ReportGeneration | null ): Promise<string | null> =>{
  const checkedUserProperties = userPropertiesValidation.checkReportGenerationParameters( userProperties );
  const features = await new CollectFeatureFiles( checkedUserProperties ).collectFeatures();
  const jsonFiles = await new CollectJsons( checkedUserProperties ).createJoinedJson();
  const jsonReport = await new SuiteFactory( checkedUserProperties ).parseJsons( jsonFiles, features );
  let reportId: string | null = null;
  if ( checkedUserProperties.saveReportInMongoDb ) {
    reportId = await insertReportIntoDatabase( jsonReport, checkedUserProperties );
  }
  await generateHtmlReport( checkedUserProperties, jsonReport );
  return reportId;
};

import * as CommonFunctions from '../common-functions/common-functions';
import type * as Models from '../models/models';
import type * as messages from '@cucumber/messages';
import { GherkinStreams } from '@cucumber/gherkin-streams';

export class CollectFeatureFiles {
  private readonly featuresDir: string | undefined;

  public constructor( reportGeneration?: Models.ReportGeneration ) {
    this.featuresDir = reportGeneration?.featuresFolder;
  }

  public async collectFeatures (): Promise<messages.GherkinDocument[] | null> {
    const result = CommonFunctions.checkFolder( this.featuresDir );

    if ( !result ) {
      return <messages.GherkinDocument[]>[];
    }

    const files = await CommonFunctions.getFilesAsync( this.featuresDir! );
    const featureFiles = files.filter( ( file ) => file.endsWith( '.feature' ) );

    const features = await Promise.all(
      featureFiles.map( async ( feature ) => {
        const stream = GherkinStreams.fromPaths( [ feature ], {} );

        const gherkinDocument = ( await CommonFunctions.streamToArray( stream ) )[ 1 ].gherkinDocument;
        return gherkinDocument;
      } ),
    );

    const filteredFeatures = features.filter( ( feature ): feature is messages.GherkinDocument => Boolean( feature ) );

    return filteredFeatures.length ? filteredFeatures : null;
  }
}

import * as CommonFunctions from '../common-functions/common-functions';
import * as Messages from '../helpers/console-messages';
import type * as Models from '../models/models';

export class CollectJsons {
  private readonly userProperties: Models.ReportGeneration;

  public constructor( userProperties: Models.ReportGeneration ) {
    this.userProperties = userProperties;
  }

  public async createJoinedJson (): Promise<Models.Feature[]> {
    const jsonOutput = Array<Models.Feature>();

    const fileList = await this.getJsonReportFiles();

    if ( !fileList.length ) {
      throw new Error( Messages.noJsonFilesProvided( this.userProperties.jsonDir )  );
    }

    fileList.forEach( json => {
      json.forEach( feature => {
        feature.id = feature.id?.toString().replace( '/', '_' );
        jsonOutput.push( feature );
      } );
    } );

    if ( this.userProperties.saveCollectedJSON! ) {
      await CommonFunctions.saveJsonFile( this.userProperties.reportPath, '/merged-output.json', jsonOutput );
    }

    return jsonOutput;
  }

  private readonly getJsonReportFiles = async (): Promise<( Models.Feature[] )[]> => {
    let files = await CommonFunctions.getFilesAsync( this.userProperties.jsonDir );
    files = files.sort();
    const validReportsList = files.filter( file => file.endsWith( '.json' ) );

    const validReports = await Promise.all(
      validReportsList.map( async element => {
        const result = await CommonFunctions.readJsonFile<Models.Feature[]>( element );
        const resultFiltered = Array.isArray( result )
          ? result.filter( feature => typeof feature.id !== 'undefined' && typeof feature.keyword !== 'undefined' )
          : null;
        return Array.isArray( resultFiltered ) && resultFiltered.length ? resultFiltered : null;
      } )
    );
    const filteredReports: Models.Feature[][] = validReports.filter( ( feature ): feature is Models.Feature[] => feature !== null );
    return filteredReports;
  };
}

