import * as CommonFunctions from '../../common-functions/common-functions';
import * as Messages from '../../helpers/console-messages';
import * as Models from '../../models/models';
import * as messages from '@cucumber/messages';
import { FeatureFormatter } from './feature-formatter';
import { v4 as uuidv4 } from 'uuid';
import GherkinDocument = messages.GherkinDocument;
import IFeature = messages.Feature;

export class SuiteFactory {
  private readonly userProperties: Models.ReportGeneration;
  private readonly suite: Models.ExtendedReport;

  public constructor( userProperies: Models.ReportGeneration ) {
    this.userProperties = userProperies;
    this.suite = this.initializeSuite();
  }

  public async parseJsons ( collectedJsons: Models.Feature[], providedFeatures: GherkinDocument[] | null ): Promise<Models.ExtendedReport> {
    const features = await Promise.all(
      collectedJsons.map( jsonFeature => {
        const gherkinFeature = this.getGherkinFeature( providedFeatures, jsonFeature.name );
        const feature = new FeatureFormatter( gherkinFeature, jsonFeature ).parseFeature();
        this.updateSuiteStadistics( feature );

        if ( !feature.elements?.length ) {
          console.log( Messages.featureRemoved( feature.name ) );
          return null;
        }
        return feature;
      } )
    );
    this.suite.features = features.flatMap( feature => feature ? [ feature ] : [] );

    this.updateSuiteProperties();
    this.suite.results.overview.result = [ ...new Map( this.suite.results.overview.result.map( item => [ item.status, item ] ) ).values() ];
    this.suite.results.overview.resultStatusesJoined = this.suite.results.overview.result.map( ( value ) => value.status.toString() ).sort().join( ';' );

    if ( this.suite.features.some( feature => feature.metadata?.length ) ) {
      this.suite.haveFeaturesMetadata = true;
    }

    if ( this.userProperties.saveEnrichedJSON ) {
      await this.saveEnrichedJson();
    }

    return this.suite;
  }

  private updateSuiteProperties (): void {
    this.suite.features = this.suite.features.sort( ( firstFeature, secondFeature ) => ( firstFeature.id! > secondFeature.id! ? 1 : -1 ) );
    this.addMissingMetadataToFeatures();
    this.suite.results.overview.durationHHMMSS = CommonFunctions.convertTimeFromNanoSecondsToHHMMSS( this.suite.results.overview.duration );
    this.suite.metadata = CommonFunctions.isMetadata( this.userProperties.reportMetadata ) ? this.userProperties.reportMetadata : <Models.Metadata[]>[];
    this.suite.features = this.setUniqueIdForEachFeature( this.suite.features );
  }

  private async saveEnrichedJson (): Promise<void> {
    await CommonFunctions.saveJsonFile( this.userProperties.reportPath, 'enriched-output.json', this.suite );
  }

  private getGherkinFeature ( providedFeatures: GherkinDocument[] | null, jsonFeatureName: string ): IFeature | null | undefined {
    return providedFeatures?.length ? providedFeatures.filter( document => document.feature?.name === jsonFeatureName )[ 0 ]?.feature : null;
  }

  private updateSuiteStadistics ( feature: Models.Feature ): void {
    const overview = feature.results.overview;
    this.suite.results.overview.duration += overview.duration;
    this.suite.results.features.passed += overview.result.filter( result => result.status === Models.Status.passed ).length === overview.result.length && overview.result.length ? 1 : 0;
    this.suite.results.features.failed += overview.result.filter( result => result.status === Models.Status.failed ).length === overview.result.length && overview.result.length ? 1 : 0;
    this.suite.results.features.pending += overview.result.filter( result => result.status === Models.Status.pending ).length === overview.result.length && overview.result.length ? 1 : 0;
    this.suite.results.features.skipped += overview.result.filter( result => result.status === Models.Status.skipped ).length === overview.result.length && overview.result.length ? 1 : 0;
    this.suite.results.features.ambiguous += overview.result.filter( result => result.status === Models.Status.ambiguous ).length === overview.result.length && overview.result.length ? 1 : 0;
    this.suite.results.features.undefined += overview.result.filter( result => result.status === Models.Status.undefined ).length === overview.result.length && overview.result.length ? 1 : 0;
    this.suite.results.features.various += Number( [ ... new Set( overview.result ) ].length > 1 );
    this.suite.results.features.total++;

    this.suite.results.scenarios = CommonFunctions.updateResultsAndPercentages( this.suite.results.scenarios, feature.results.scenarios );
    this.suite.results.steps = CommonFunctions.updateResultsAndPercentages( this.suite.results.steps, feature.results.steps );
    this.suite.results.features = <Models.FeatureModuleResults>CommonFunctions.updatePercentages( this.suite.results.features );
    this.suite.results.overview.result.push( ...feature.results.overview.result );
  }

  private initializeSuite (): Models.ExtendedReport {
    const suite = <Models.ExtendedReport>{};
    suite.results = Models.reportResultsInitializer();
    suite.metadata = <Models.Metadata[]>[];
    suite.metadataTitle = this.userProperties.reportMetadataTitle;
    suite.reportTitle = this.userProperties.reportTitle;
    suite.haveFeaturesMetadata = false;
    return suite;
  }

  private setUniqueIdForEachFeature ( features: Models.Feature[] ): Models.Feature[] {
    const seen = new Set();
    const modifiedFeatures = features.map( feature => {
      if ( seen.size === seen.add( feature.id ).size ) {
        feature.id += uuidv4();
      }
      return feature;
    } );
    return modifiedFeatures;
  }

  private addMissingMetadataToFeatures (): void {
    const allMetadata: Models.Metadata[] = [];
    this.suite.features.forEach( feature => {
      if ( feature.metadata?.length ) {
        feature.metadata.forEach( metadataElement => {
          if ( !allMetadata.filter( metadataObject => metadataObject.name === metadataElement.name ).length ) {
            allMetadata.push( metadataElement );
          }
        } );
      }
    } );
    this.suite.results.overview.metadata = allMetadata;

    this.suite.features.forEach( feature => {
      allMetadata.forEach( metadataElement => {
        const featureMetadataName = feature.metadata?.filter( featureMetadataElement => featureMetadataElement.name === metadataElement.name );
        if ( !featureMetadataName?.length ) {
          feature.metadata!.push( { name: metadataElement.name, value: '' } );
        }
      } );
    } );
  }
}

import * as CommonFunctions from '../../common-functions/common-functions';
import * as Models from '../../models/models';
import type * as messages from '@cucumber/messages';
import { ScenarioFormatter } from './scenario-formatter';

export class FeatureFormatter {
  private readonly gherkinFeature: messages.Feature | null | undefined;
  private jsonFeature: Models.Feature;
  private readonly scenarioFormater: ScenarioFormatter;
  private readonly scenarioOutlines: Models.ScenarioOutline[];

  public constructor( gherkinFeature: messages.Feature | null | undefined, jsonFeature: Models.Feature ) {
    this.gherkinFeature = gherkinFeature;
    this.jsonFeature = jsonFeature;
    this.scenarioFormater = new ScenarioFormatter( this.gherkinFeature );
    this.scenarioOutlines = this.getOutlineScenarios();
  }

  public parseFeature (): Models.Feature {
    this.initalizeFeature();

    if ( this.jsonFeature.elements?.length ) {
      this.joinOutlineScenarios();
      for ( let index = 0; index < this.jsonFeature.elements.length; index++ ) {
        const jsonScenario = this.scenarioFormater.parseScenario( this.jsonFeature.elements[ index ], this.jsonFeature.name );
        if ( jsonScenario !== null && typeof jsonScenario !== 'undefined' ) {
          this.jsonFeature.elements[ index ] = jsonScenario;
          this.updateFeatureStadistics( this.jsonFeature.elements[ index ]?.results );
          const gherkinScenario = this.gherkinFeature?.children.filter(
            scenario => scenario.scenario?.name === jsonScenario.name )[ 0 ]?.scenario;
          if ( this.isLastScenarioOutlineWithFeature( jsonScenario, index ) && gherkinScenario ) {
            const scenarios = this.addFirstScenarioOutline( this.jsonFeature.elements, jsonScenario, index, gherkinScenario );
            this.jsonFeature.elements = scenarios;
            index++;
          }
        }
      }

      this.jsonFeature.results.overview.durationHHMMSS =
        CommonFunctions.convertTimeFromNanoSecondsToHHMMSS( this.jsonFeature.results.overview.duration );
      this.jsonFeature.elements = this.jsonFeature.elements.filter( scenario => Object.keys( scenario ).length && scenario.steps?.length );
    }

    return this.jsonFeature;
  }

  private addFirstScenarioOutline ( scenarios: Models.Scenario[], jsonScenario: Models.Scenario, index: number, gherkinScenario: messages.Scenario ): Models.Scenario[] {
    const scenarioName = jsonScenario.name;
    const scenarioOutlineLengthWithoutHeaders = this.scenarioOutlines.filter( scenario => scenario.name === scenarioName ).length - 1;
    const scenarioOutline = this.scenarioFormater.createNewFirstScenarioOutline( jsonScenario, gherkinScenario, scenarios, index - scenarioOutlineLengthWithoutHeaders );
    scenarios.splice( index - scenarioOutlineLengthWithoutHeaders, 0, scenarioOutline );

    return scenarios;
  }

  private updateFeatureStadistics ( scenarioResults: Models.ScenarioResults ): void {
    this.jsonFeature.results.scenarios.undefined += scenarioResults.steps.undefined ? 1 : 0;
    this.jsonFeature.results.scenarios.failed += !scenarioResults.steps.undefined && scenarioResults.steps.failed ? 1 : 0;
    this.jsonFeature.results.scenarios.ambiguous += scenarioResults.steps.ambiguous &&
      !scenarioResults.steps.undefined &&
      !scenarioResults.steps.failed ? 1 : 0;
    this.jsonFeature.results.scenarios.pending += scenarioResults.steps.pending &&
      !scenarioResults.steps.failed &&
      !scenarioResults.steps.ambiguous &&
      !scenarioResults.steps.undefined ? 1 : 0;
    this.jsonFeature.results.scenarios.skipped += scenarioResults.steps.skipped &&
      !scenarioResults.steps.failed &&
      !scenarioResults.steps.ambiguous &&
      !scenarioResults.steps.pending &&
      !scenarioResults.steps.undefined ? 1 : 0;
    this.jsonFeature.results.scenarios.passed +=
      scenarioResults.steps.passed &&
        !scenarioResults.steps.failed &&
        !scenarioResults.steps.skipped &&
        !scenarioResults.steps.pending &&
        !scenarioResults.steps.ambiguous &&
        !scenarioResults.steps.undefined ? 1 : 0;
    this.jsonFeature.results.scenarios.total =
      this.jsonFeature.results.scenarios.passed +
      this.jsonFeature.results.scenarios.skipped +
      this.jsonFeature.results.scenarios.pending +
      this.jsonFeature.results.scenarios.undefined +
      this.jsonFeature.results.scenarios.ambiguous +
      this.jsonFeature.results.scenarios.failed;
    this.jsonFeature.results.scenarios = CommonFunctions.updatePercentages( this.jsonFeature.results.scenarios );
    this.jsonFeature.results.steps = CommonFunctions.updateResultsAndPercentages( this.jsonFeature.results.steps, scenarioResults.steps );
    this.jsonFeature.results.overview.result = CommonFunctions.updateStatus( this.jsonFeature.results );
    this.jsonFeature.results.overview.duration += scenarioResults.overview.duration;
  }

  private initalizeFeature (): void {
    this.jsonFeature.description = typeof this.jsonFeature.description === 'undefined' ? '' : this.jsonFeature.description;
    this.jsonFeature.elements = this.jsonFeature.elements === null || typeof this.jsonFeature.elements === 'undefined' ? [] : this.jsonFeature.elements;
    this.jsonFeature.results = Models.featureResultsInitializer();
    this.jsonFeature.tags = typeof this.jsonFeature.tags === 'undefined' ? [] : this.jsonFeature.tags;
    this.jsonFeature.metadataTitle = this.jsonFeature.metadataTitle ?? 'Custom Data';
    this.jsonFeature.metadata = CommonFunctions.isMetadata( this.jsonFeature.metadata ) ? this.jsonFeature.metadata : [];
    this.jsonFeature.line = this.jsonFeature.line ?? 0;
    this.joinOutlineScenarios();
  }

  private joinOutlineScenarios (): void {
    const scenarioOutlines: {
      index: number;
      name: string;
    }[] = [];
    this.jsonFeature.elements?.forEach( scenario => {
      if ( this.scenarioFormater.isOutlineScenario( scenario ) ) {
        scenarioOutlines.push( {
          index: parseInt( `${( this.jsonFeature.elements! ).indexOf( scenario )}`, 10 ),
          name: `${scenario.name}`
        } );
      }
    } );
    const scenarioOutlineNames = [ ...new Set( scenarioOutlines.map( item => item.name ) ) ];
    scenarioOutlineNames.forEach( name => {
      const scenarioOutlineElements = scenarioOutlines.filter( scenario => scenario.name === name );
      const firstScenarioIndex = scenarioOutlineElements[ 0 ].index;
      scenarioOutlineElements.forEach( ( scenarioOutlineElement, index ) => {
        CommonFunctions.arraymove( this.jsonFeature.elements!, scenarioOutlineElement.index, firstScenarioIndex + index );
      } );
    } );
  }

  private isLastScenarioOutlineWithFeature ( scenario: Models.Scenario, index: number  ): boolean {
    const ocurrences = this.scenarioOutlines.filter( scenarioElement => scenarioElement.name === scenario.name );
    if ( ocurrences[ ocurrences.length - 1 ]?.index === index ) {
      return true;
    }
    return false;
  }

  private getOutlineScenarios (): Models.ScenarioOutline[] {
    const scenarioOutlines: Models.ScenarioOutline[] = [];
    this.jsonFeature.elements?.forEach( scenario => {
      if ( this.scenarioFormater.isOutlineScenario( scenario ) ) {
        scenarioOutlines.push( {
          index: parseInt( `${( this.jsonFeature.elements! ).indexOf( scenario )}`, 10 ),
          name: `${scenario.name}`
        } );
      }
    } );

    return scenarioOutlines;
  }
}

import * as CommonFunctions from '../../common-functions/common-functions';
import * as ConsoleMessages from '../../helpers/console-messages';
import * as Models from '../../models/models';
import * as lodash from 'lodash';
import * as messages from '@cucumber/messages';
import { StepFormatter } from './step-formatter';
import IFeature = messages.Feature;
import Scenario = messages.Scenario;
import IStep = messages.Step;
import ITableRow = messages.TableRow;
import ITableCell = messages.TableCell;

export class ScenarioFormatter {
  private readonly gherkinFeature: IFeature | null | undefined;

  public constructor( gherkinFeature: IFeature | null | undefined ) {
    this.gherkinFeature = gherkinFeature;
  }

  public parseScenario ( scenario: Models.Scenario, featureName: string ): Models.Scenario | null {
    let localScenario = this.initializeScenario( scenario, featureName );
    if ( localScenario ) {
      localScenario = this.parseSteps( localScenario );
      localScenario.results = this.updateScenarioResults( localScenario.results );
    }
    return localScenario;
  }

  public createNewFirstScenarioOutline (
    jsonScenario: Models.Scenario,
    gherkinScenario: Scenario,
    jsonScenarios: Models.Scenario[],
    scenarioIndex: number ): Models.Scenario {

    const firstScenarioOutline: Models.Scenario = lodash.cloneDeep( jsonScenario );
    firstScenarioOutline.id = `${firstScenarioOutline.id.substring( 0, firstScenarioOutline.id.length - 3 )};;1`;
    firstScenarioOutline.name = CommonFunctions.escapeHTML( firstScenarioOutline.name );
    firstScenarioOutline.before = <Models.BeforeOrAfter>{ steps: <Models.Step[]>[] };
    firstScenarioOutline.after = <Models.BeforeOrAfter>{ steps: <Models.Step[]>[] };
    firstScenarioOutline.results = Models.scenarioResultsInitializer();
    firstScenarioOutline.steps = this.initializeScenarioOutlineSteps( firstScenarioOutline.steps!, gherkinScenario );
    firstScenarioOutline.examples = this.initializeExamples( gherkinScenario, jsonScenarios );

    firstScenarioOutline.examples.forEach( ( example, index, arr ) => {
      if ( index < arr.length - 1 ) {
        firstScenarioOutline.results.overview.duration += jsonScenarios[ scenarioIndex + index ].results.overview.duration;
        firstScenarioOutline.results.steps.passed += jsonScenarios[ scenarioIndex + index ].results.steps.passed;
        firstScenarioOutline.results.steps.failed += jsonScenarios[ scenarioIndex + index ].results.steps.failed;
        firstScenarioOutline.results.steps.undefined += jsonScenarios[ scenarioIndex + index ].results.steps.undefined;
        firstScenarioOutline.results.steps.pending += jsonScenarios[ scenarioIndex + index ].results.steps.pending;
        firstScenarioOutline.results.steps.ambiguous += jsonScenarios[ scenarioIndex + index ].results.steps.ambiguous;
        firstScenarioOutline.results.steps.skipped += jsonScenarios[ scenarioIndex + index ].results.steps.skipped;
      }
    } );

    firstScenarioOutline.results.overview.durationHHMMSS = CommonFunctions.convertTimeFromNanoSecondsToHHMMSS(
      firstScenarioOutline.results.overview.duration
    );

    firstScenarioOutline.isFirstScenarioOutline = true;
    return firstScenarioOutline;
  }

  public isOutlineScenario ( scenario: Models.Scenario ): boolean {
    if ( this.gherkinFeature?.children.some( scenarioFromFeature =>
      scenarioFromFeature.scenario?.name === scenario.name
      && scenarioFromFeature.scenario.examples.length
      && scenarioFromFeature.scenario.steps.length ) ) {
      return true;
    }
    return false;
  }

  private initializeExamples ( gherkinScenario: Scenario, jsonScenarios: Models.Scenario[] ): string[][] {
    const examples = gherkinScenario.examples[ 0 ];
    const headerRow = examples.tableHeader;
    const examplesHeaderCells = headerRow?.cells.map( cell => cell.value ) !;
    const examplesRows = examples.tableBody.map( row => {
      if ( this.isRowExecuted( headerRow, row.cells, gherkinScenario.steps, jsonScenarios ) ) {
        const rowContent: string[] = [];
        ( row.cells ).forEach( cell => {
          rowContent.push( cell.value );
        } );
        return rowContent;
      }
      return null;
    } ).flatMap( row => row ? [ row ] : [] );
    let rows = <string[][]>[];
    rows.push( examplesHeaderCells );
    rows = rows.concat( examplesRows );
    return rows;
  }

  private initializeScenarioOutlineSteps ( steps: Models.Step[], gherkinScenario: Scenario ): Models.Step[]{
    steps.forEach( ( stepJs: Models.Step ) => {
      delete stepJs.text;
      stepJs.rows = [];
      delete stepJs.result.error_message;
      stepJs.embeddings = [];
      stepJs.attachments = [];
      stepJs.result.status = Models.Status.passed;
      ( gherkinScenario.steps ).map( stepPd => {
        if ( ( stepPd.location ).line === stepJs.line ) {
          stepJs.name = CommonFunctions.escapeHTML( String( stepPd.text ) );
        }
        return stepPd;
      } );
    } );
    return steps;
  }

  private parseSteps ( scenario: Models.Scenario ): Models.Scenario {
    scenario.steps?.map( step => {
      const parsedStep = new StepFormatter().parseStep( step );
      if ( parsedStep.keyword.trim() === 'Before' ) {
        scenario.before?.steps.push( parsedStep );
        scenario.before!.results = this.updateBeforeOrAfterStadistics( scenario.before!.results, parsedStep.result );
        scenario.results.before.duration += parsedStep.result.duration;
      }
      if ( parsedStep.keyword.trim() === 'After' ) {
        scenario.after?.steps.push( parsedStep );
        scenario.after!.results = this.updateBeforeOrAfterStadistics( scenario.after!.results, parsedStep.result );
        scenario.results.after.duration += parsedStep.result.duration;
      }

      scenario.results = this.updateScenarioStepsStadistics( scenario.results, parsedStep.result );

      return parsedStep;
    } );
    scenario.steps = scenario.steps?.filter( step => step.keyword.trim() !== 'Before' && step.keyword.trim() !== 'After' );
    return scenario;
  }

  private initializeScenario ( scenario: Models.Scenario, featureName: string ): Models.Scenario | null {
    if ( typeof scenario.id === 'undefined' ) {
      console.log( ConsoleMessages.scenarioWithoutIdRemoved( scenario.name, featureName ) );
      return null;
    }

    if ( !scenario.steps?.length ) {
      console.log( ConsoleMessages.scenarioWithoutStepsRemoved( scenario.name, featureName ) );
      return null;
    }

    if ( this.isOutlineScenario( scenario ) ) {
      scenario.keyword = 'Scenario Outline';
    }
    scenario.isFirstScenarioOutline = false;
    scenario.examples = [];
    scenario.keyword = typeof scenario.keyword === 'undefined' ? '' : scenario.keyword;
    scenario.name = typeof scenario.name === 'undefined' ? '' : scenario.name;
    scenario.type = typeof scenario.type === 'undefined' ? 'scenario' : scenario.type;
    scenario.results = Models.scenarioResultsInitializer();
    scenario.before = Models.beforeOrAfterInitializer();
    scenario.after = Models.beforeOrAfterInitializer();
    scenario.tags = typeof scenario.tags === 'undefined' ? [] : scenario.tags;
    return scenario;
  }

  private updateBeforeOrAfterStadistics ( results: Models.BeforeOrAfterResults, stepResults: Models.StepResultsOverview ): Models.BeforeOrAfterResults {
    results.overview.duration += stepResults.duration;
    results.overview.status = stepResults.status === Models.Status.passed
      ? results.overview.status
      : stepResults.status;
    return results;
  }

  private updateScenarioStepsStadistics ( results: Models.ScenarioResults, stepResults: Models.StepResultsOverview ): Models.ScenarioResults {
    results.overview.duration += stepResults.duration ? stepResults.duration : 0;
    results.overview.status = stepResults.status === Models.Status.failed ? results.overview.status : Models.Status.failed;

    switch ( stepResults.status ) {
    case Models.Status.passed:
      results.steps.passed += 1;
      break;
    case Models.Status.failed:
      results.steps.failed += 1;
      break;
    case Models.Status.ambiguous:
      results.steps.ambiguous += 1;
      break;
    case Models.Status.pending:
      results.steps.pending += 1;
      break;
    case Models.Status.skipped:
      results.steps.skipped += 1;
      break;
    case Models.Status.undefined:
      results.steps.undefined += 1;
      break;
      // no default
    }

    results.steps.total += 1;
    return results;
  }

  private updateScenarioResults ( scenarioResults: Models.ScenarioResults ): Models.ScenarioResults {
    scenarioResults.overview.durationHHMMSS = CommonFunctions.convertTimeFromNanoSecondsToHHMMSS( scenarioResults.overview.duration );
    scenarioResults.before.durationHHMMSS = CommonFunctions.convertTimeFromNanoSecondsToHHMMSS( scenarioResults.before.duration );
    scenarioResults.after.durationHHMMSS = CommonFunctions.convertTimeFromNanoSecondsToHHMMSS( scenarioResults.after.duration );
    scenarioResults.steps = CommonFunctions.updatePercentages( scenarioResults.steps );
    return scenarioResults;
  }

  private isRowExecuted ( tableHeader: ITableRow | undefined, cells: readonly ITableCell[], steps: readonly IStep[], jsonScenarios: Models.Scenario[] ): boolean {
    const foundSteps = steps.filter( step => {
      let stepText = step.text;
      tableHeader?.cells.forEach( ( cell, index ) => {
        stepText = stepText.replace( `<${cell.value}>`, ( ( cells[ index ] ).value ) );
      } );
      return jsonScenarios.some( scenario => scenario.steps?.some( jsonStep => jsonStep.name === stepText ) );
    } ).length;
    return foundSteps === steps.length;
  }
}

import * as CommonFunctions from '../../common-functions/common-functions';
import * as Models from '../../models/models';

export class StepFormatter {
  public parseStep ( step: Models.Step ): Models.Step {
    let localStep = step;
    localStep = this.initializeStep( localStep );
    localStep = this.formatStepEmbeddings( localStep );
    if( typeof step.rows !== 'undefined' && step.rows.length ){
      step.rowsCells = this.formatRows( step.rows );
    }
    step.argumentsCells = this.formatRows( step.arguments?.[0]?.rows );
    this.setColorAndIcon( step );
    return localStep;
  }

  private formatStepEmbeddings ( step: Models.Step ): Models.Step {
    if ( step.embeddings.length ) {
      step.embeddings.forEach( ( embedding: Models.Embedding, embeddingIndex: number ) => {
        let embeddingType = typeof embedding.mime_type === 'undefined' ? embedding.media?.type : embedding.mime_type ;
        let embeddingData: string = embedding.data;

        switch ( embeddingType ){
        case 'application/json':
          try {
            embeddingData = JSON.stringify( JSON.parse( embeddingData ), undefined, 2 );
          } catch ( error: unknown ){
            embeddingData = ( <Error>error ).message;
          }
          step.json = step.json.concat( [ embeddingData ] );
          break;

        case 'text/html':
          step.html = step.html.concat( [ CommonFunctions.isBase64( embeddingData ) ? Buffer.from( embeddingData, 'base64' ).toString( 'utf-8' ) : embeddingData ] );
          break;

        case 'text/plain':
          step.text = ( step.text ? step.text : [] ).concat( [
            CommonFunctions.isBase64( embeddingData ) ? CommonFunctions.escapeHTML( Buffer.from( embeddingData, 'binary' ).toString( 'base64' ) ) : CommonFunctions.escapeHTML( embeddingData )
          ] );
          break;

        case 'image/png':
          embeddingType.split( ':' );
          step.image = step.image.concat( [ `data:image/png;base64,${  embeddingData}` ] );
          step.embeddings[ embeddingIndex ] = <Models.Embedding>{} ;
          break;

        case 'audio/ogg':
          step.audio = step.audio.concat( [ `data:audio/mpeg;base64,${  embeddingData}` ] );
          step.embeddings[ embeddingIndex ] = <Models.Embedding>{};
          break;

        case 'video/ogg':
          step.video = step.video.concat( [ `data:video/mpeg;base64,${  embeddingData}` ] );
          step.embeddings[ embeddingIndex ] = <Models.Embedding>{};
          break;

        default:
          embeddingType = 'text/plain';
          if ( embedding.mime_type ) {
            embeddingType = embedding.mime_type;
          } else if ( embedding.media?.type ) {
            embeddingType = embedding.media.type;
          }
          step.attachments.push( {
            data: `data:${embeddingType};base64,${embedding.data}`,
            type: embeddingType
          } );
        }
        step.embeddings[ embeddingIndex ] = <Models.Embedding>{};
      } );
    }
    return step;
  }

  private formatRows ( rows: Models.Row[] | undefined ): string {
    let rowsTemp = '';
    if ( typeof rows !== 'undefined' && rows.length ) {
      rowsTemp = '<table class=\'table-bordered table-dark ms-4\'>';
      rows.forEach( ( row, rowIndex ) => {
        rowsTemp += '<tr\\>';
        row.cells.forEach( ( cell ) => {
          if ( rowIndex === 0 ) {
            rowsTemp += `<td class=bg-primary> ${cell} </td>`;
          } else {
            rowsTemp += `<td> ${cell} </td>`;
          }
        } );
        rowsTemp += '</tr>';
      } );
      rowsTemp = `${rowsTemp} </table>`;
    }
    return rowsTemp;
  }

  private setColorAndIcon ( step: Models.Step ): void{
    switch ( step.result.status ){
    case Models.Status.passed:
      step.result.color = ' passed-color';
      step.result.icon = 'fa fa-check-circle fa-1x';
      step.result.title = 'Step passed';
      break;
    case Models.Status.failed:
      step.result.color = ' failed-color';
      step.result.icon = ' fa fa-exclamation-circle fa-1x ';
      step.result.title = 'Step failed';
      break;
    case Models.Status.ambiguous:
      step.result.color = 'ambiguous-color';
      step.result.icon = ' fas fa-bolt fa-1x ';
      step.result.title = 'Step has double step implementation and failed because of that';
      break;
    case Models.Status.pending:
      step.result.color = 'pending-color';
      step.result.icon = ' fa fa-minus-circle fa-1x ';
      step.result.title = 'Step is pending';
      break;
    case Models.Status.skipped:
      step.result.color = 'skipped-color';
      step.result.icon = ' fas fa-arrow-circle-right fa-1x ';
      step.result.title = 'Step is skipped';
      break;
    case Models.Status.undefined:
      step.result.color = 'undefined-color';
      step.result.icon = ' fa fa-question-circle fa-1x ';
      step.result.title = 'Step passed';
      break;
    // No default
    }
  }

  private initializeStep( step: Models.Step ): Models.Step{
    step.audio = [];
    step.attachments = [];
    step.arguments = typeof step.arguments === 'undefined' ? []: step.arguments;
    step.embeddings = typeof step.embeddings === 'undefined' ? [] : step.embeddings;
    step.examples = [];
    step.html = [];
    step.json = [];
    step.id = '';
    step.line = typeof step.line === 'undefined' ? -1 : step.line;
    step.image = [];
    step.result.duration = typeof step.result.duration === 'undefined' ? 0 : step.result.duration;
    step.result.durationHHMMSS = CommonFunctions.convertTimeFromNanoSecondsToHHMMSS( step.result.duration );
    step.rows = typeof step.rows === 'undefined' ? [] : step.rows;
    step.text = null;
    step.output = typeof step.output === 'undefined' ? [] : step.output;
    step.video = [];
    if( step.match ){
      step.match.arguments = typeof step.match.arguments === 'undefined' ? []: step.match.arguments;
      step.match.location = '';
    }
    return step;
  }
}

import * as CommonFunctions from '../common-functions/common-functions';
import * as Messages from './console-messages';
import type * as Models from '../models/models';

class ApplicationPropertiesValidation {
  public checkReportGenerationParameters ( reportGenerationParameters: Models.ReportGeneration | null ): Models.ReportGeneration {
    if ( !reportGenerationParameters ) {
      throw new Error( Messages.optionsNotProvided );
    }

    CommonFunctions.checkFolder( reportGenerationParameters.jsonDir );

    return this.initializeReportGenerationParameters( reportGenerationParameters );
  }

  public initializeReportGenerationParameters ( reportGenerationParameters: Models.ReportGeneration ): Models.ReportGeneration {
    reportGenerationParameters.mongooseServerUrl = reportGenerationParameters.mongooseServerUrl ?? 'http://localhost:3000';
    reportGenerationParameters.reportTitle = reportGenerationParameters.reportTitle ?? 'Cucumber HTML Report Generator';
    reportGenerationParameters.reportPath = CommonFunctions.initializePath( reportGenerationParameters.reportPath );
    return reportGenerationParameters;
  }

  public initializeReportDisplayParameters ( reportConfigurationParameters: Models.ReportDisplay | undefined ): Models.ReportDisplay {
    const localReportDisplayParameters = reportConfigurationParameters ?? <Models.ReportDisplay>{};
    localReportDisplayParameters.theme = localReportDisplayParameters.theme === 'Light' ? localReportDisplayParameters.theme : 'Dark' ;
    localReportDisplayParameters.reportPath = CommonFunctions.initializePath( localReportDisplayParameters.reportPath );

    return localReportDisplayParameters;
  }
}

export const userPropertiesValidation = new ApplicationPropertiesValidation();
